import React from 'react';
import {Component, Fragment} from 'react';
import { Button } from 'semantic-ui-react'
import {Switch, Route} from 'react-router-dom'
import 'semantic-ui-css/semantic.min.css'
import { connect } from 'react-redux';

import './App.css';
import { postToArtistCreate } from './actions/sessionActions'
import ArtistSignup from './components/ArtistSignup'
import Dashboard from './components/Dashboard'
import Login from './components/Login'
import Subscribe from './components/FanView/Subscribe'
import SubscriptionPage from './components/ArtistViews/SubscriptionPage'
import Settings from './components/ArtistViews/Settings'
import Blasts from './components/ArtistViews/Blasts'
import Navbar from './components/Navbar'
import Home from './components/Home'
import {LOGIN, autoLogin} from './actions/sessionActions'




class App extends Component {

  componentDidMount(){
    this.checkForToken()
  }

  renderSomething = () => {
    if(this.props.sessionsReducer.authenticated){
      return <Navbar />
    }
  }

  checkForToken = () => {
    const token = localStorage.token
    return token ? this.props.fetchArtistCreate(autoLogin(token)) : null
  }

  slugParams = ({match}) => {
    return <h1>SUCCESS</h1>
  }


  render(){
    // console.log("APP PROPS", this.props);
    console.log("CURRENT USER IN APP.JS", this.props);
    if(this.props.sessionsReducer.loading || this.props.sessionsReducer.currentUser){
      return <h1>loading....</h1>
    } else {
      return (
        <Fragment>
          {this.renderSomething()}
          <Switch>
            <Route path='/dashboard' component={Dashboard}/>
            <Route path='/subscribe/edit' component={SubscriptionPage}/>
            <Route path='/subscribe/:slug' component={Subscribe}/>
            <Route path='/settings' component={Settings}/>
            <Route path='/blasts' component={Blasts}/>
            <Route exact path='/' component={Home}/>
          </Switch>
        </Fragment>
      );
    }
  }
}

const mapStateToProps = (state) => {
  return state
}

const mapDispatchToProps = (dispatch) => {
  return {fetchArtistCreate: (token)=> dispatch(postToArtistCreate(token, "auto_login"))}
}

export default connect(mapStateToProps, mapDispatchToProps)(App);



