export {SUBSCRIPTION_SUCCESS, setSubscriptionSuccess, SUBSCRIPTION_FAILED, setSubscriptionFail }

const SUBSCRIPTION_SUCCESS = "SUBSCRIPTION_SUCCESS"
const SUBSCRIPTION_FAILED = "SUBSCRIPTION_FAILED"

const setSubscriptionSuccess = (payload) => {
  return {type: SUBSCRIPTION_SUCCESS, payload}
}

const setSubscriptionFail = (payload) => {
  return {type: SUBSCRIPTION_FAILED, payload}
}


/****************************************************************
FETCH
****************************************************************/
export const postToSubscriptionCreate = (credentials) => {

  return (dispatch) => {
    dispatch( { type: "LOADING" } );
    return fetch(`http://localhost:3000/api/v1/subscribe`, {
      method: 'POST',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(credentials)
    })
    .then(response => response.json())
      .then(data => {
      if (data.error){
        dispatch({ type: "SUBSCRIPTION_ERROR", payload: data })
      } else {
        if(data.status === "Success"){
          dispatch({ type: SUBSCRIPTION_SUCCESS, payload: data })
        }
      }
    }
  )
  .catch(error => {
    return error;
  })
}
}
