import React, { Component } from 'react';
import {Card, Icon} from 'semantic-ui-react'

const BlastCard = (props) => {
  return (
    <Card fluid>
      <Card.Content textAlign="center" header={props.blast.name} />
      <Card.Content description={props.blast.body} />
      <Card.Content extra>
        Date Sent: {props.blast.date_sent}
        Time Sent: {props.blast.time_sent}
      </Card.Content>
    </Card>
  )
}

export default BlastCard
