import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Button, Checkbox, Form, Input, Header } from 'semantic-ui-react'


import { postToSubscriptionCreate } from '../../actions/subscriptionActions'

class Subscribe extends Component {
  state={phone1: "", phone2: "", phone3: "", zip: "", slug: "", payload: {}}


  componentDidMount(){
    const slug = this.props.match.params.slug
    if(slug){
      this.setState({...this.state, slug })
      this.fetchArtistShowPage(slug)
    } else {
      this.fetchArtistShowPage(this.props.currentUser.username)
    }
  }

  fetchArtistShowPage = (slug) => {
    fetch(`http://localhost:3000/api/v1/subscribe/${slug}`)
    .then(r => r.json())
    .then(data => {
      this.setState({...this.state, payload: data.payload})
    })
  }

  handleFormChange = (fieldInputName, event) => {
    switch(fieldInputName){
      case "phone1":
        this.setState( {...this.state, phone1: event.target.value} )
      break;
      case "phone2":
        this.setState( {...this.state, phone2: event.target.value} )
      break;
      case "phone3":
        this.setState( {...this.state, phone3: event.target.value} )
      break;
      case "zip":
        this.setState( {...this.state, zip: event.target.value} )
      break;
    }
  }

  dispatchSubscription = () => {

    const isValidZip = /(^\d{5}$)|(^\d{5}-\d{4}$)/.test(this.state.zip);
    debugger
    this.props.postToSubscriptionCreate({phone: this.state.phone, zip: this.state.zip, slug: this.state.slug})
  }

  render (){
        console.log("PROPSSSSS", this.state);
    return (
      <div>
      { this.state.payload.title && <Header as='h1' textAlign='center'>{`${this.state.payload.title}`}</Header> }
      <br/>
      <br/>
      <br/>
      <br/>
      <br/>
      <br/>
      <br/>
        <Form onSubmit={this.dispatchSubscription}>
        <Form.Group inline>
          <Form.Field required>
            <label>Phone Number</label>
            <Input placeholder='(xxx)' value={this.state.phone1} maxLength="3" type="number" step="0.01" onChange={(event) => this.handleFormChange("phone1", event)}/>
          </Form.Field>
          <Form.Field required>
            <Input placeholder='xxx' value={this.state.phone2} maxLength="3" type="number" onChange={(event) => this.handleFormChange("phone2", event)}/>
          </Form.Field>
          <Form.Field required>
            <Input placeholder='xxxx' value={this.state.phone3} maxLength="4" type="number" onChange={(event) => this.handleFormChange("phone3", event)}/>
          </Form.Field>
        </Form.Group>
          <Form.Field width={8} required>
            <Input placeholder='Zipcode' value={this.state.zip} name="password" onChange={(event) => this.handleFormChange("zip", event)}/>
          </Form.Field>
          <Button type='submit'>Subscribe</Button>
        </Form>
      </div>
    )
  }
}
const mapDispatchToProps = (dispatch) => {
  console.log("SUB - DP", dispatch);
  return {postToSubscriptionCreate: (newArtist)=> dispatch(postToSubscriptionCreate(newArtist))}
}

const mapStateToProps = (state) => {
  console.log("fan", state.sessionsReducer);
  return {currentUser: state.sessionsReducer.currentUser}
}

export default connect(mapStateToProps, mapDispatchToProps)(Subscribe)
