import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Button, Checkbox, Form, Input, Header, Grid } from 'semantic-ui-react'


import { postToSubscriptionCreate } from '../../actions/subscriptionActions'

class Subscribe extends Component {
  state={phone: "", zip: "", payload: {}}


  componentDidMount(){

      // .then(r => r.json())
      // .then(data => {
      //   this.setState({...this.state, payload: data.payload})
      // })
    }

fetchShowPage = () => {
  fetch('http://localhost:3000/api/v1/subscribe/')
}

  handleFormChange = (fieldInputName, event) => {
    switch(fieldInputName){
      case "phone":
      this.setState( {...this.state, phone: event.target.value} )
      break;
      case "zip":
      this.setState( {...this.state, zip: event.target.value} )
      case "url":
      this.setState( {...this.state, url: `http://localhost:3001/subscribe/${event.target.value}`} )
      break;
    }
  }

  dispatchSubscription = () => {
    this.props.postToSubscriptionCreate(this.state)
  }


  render (){
    console.log("ARRTIST-SUB", this.props);
    return (
      <div>
      <Header as='h1' textAlign='center'>{`${this.props.subscriptionProps.title}`}</Header>
      <br/>
      <br/>
      <br/>
      <br/>
      <br/>
      <br/>
      <br/>
        <Form onSubmit={this.dispatchSubscription}>
        <Form.Group inline>
          <Grid.Row centered>
            <Form.Field required>
              <label>Phone Number</label>
              <Input disabled placeholder='(xxx)' maxLength="3" type="number" step="0.01"/>
            </Form.Field>
            <Form.Field required>
              <Input disabled placeholder='xxx' maxLength="3" type="number"/>
            </Form.Field>
            <Form.Field required>
              <Input disabled placeholder='xxxx' maxLength="4" type="number"/>
            </Form.Field>
          </Grid.Row>
        </Form.Group>
          <Form.Field required>
            <Input disabled placeholder='Zipcode' value={this.state.zip} name="password"  onChange={(event) => this.handleFormChange("zip", event)}/>
          </Form.Field>
          <Button type='submit'>Subscribe</Button>
          <Form.Field>
            <Input disabled value={this.state.url} placeholder='Enter custom url'name="password" label='http://localhost:3001/subscribe/'  onChange={(event) => this.handleFormChange("url", event)}/>
          </Form.Field>
        </Form>

      </div>
    )

  }
}
const mapDispatchToProps = (dispatch) => {
  return {postToSubscriptionCreate: (newArtist)=> dispatch(postToSubscriptionCreate(newArtist))}
}

const mapStateToProps = (state) => {
    console.log("IS PRESENT");
    return {subscriptionProps: state.sessionsReducer.currentUser.subscriptionProps}
  }


export default connect(mapStateToProps, mapDispatchToProps)(Subscribe)
