import React from 'react';
import './App.css';
import ArtistSignup from './components/ArtistSignup'
import { postToArtistCreate } from './actions/sessionActions'
import { connect } from 'react-redux';
import Dashboard from './components/Dashboard'


function App(props) {
// console.log("NEWUSER-PROPS", props);
const renderrIfLoggedIn = () => {
  if(this.props.authenticated){
    return <Dashboard />
  }
}

  return (
    <div id="sign-up-container">
      <ArtistSignup />
      {renderrIfLoggedIn()}
    </div>
  );
}

const mapStateToProps = (state) => {
  if(state.sessionsReducer.currentUser){
    const id = state.sessionsReducer.currentUser.id
    const authenticated = state.sessionsReducer.authenticated
    return {currentUser: { id , authenticated  }}
  } else {
    return state
  }


  // console.log("STTATE", state);

}

const mapDispatchToProps = (dispatch) => {
  // console.log("DISPATCH", dispatch);
}

export default connect(mapStateToProps)(App);
// export default connect(mapStateToProps, postToArtistCreate())(App);
