import { connect } from 'react-redux';
// import { Button, Checkbox, Form, Input } from 'semantic-ui-react'
import { Menu, Segment, TextArea, Form, Input } from 'semantic-ui-react'

class Blasts extends Component {
  render(){
    return (
      <Form>
        <TextArea fontSize="x-large" width={8} placeholder='Tell us more' />
        <Input placeholder='url' value={this.state.phone2} />
      </Form>
    )
  }
}

export default connect()(Blasts)
