import initialState from './initialState';
import {browserHistory} from 'react-router';
import { LOGIN_SUCCESS } from '../actions/sessionActions'

export default (state = initialState.session, action) => {

  switch(action.type) {

    case LOGIN_SUCCESS:
      // browserHistory.push('/')
      localStorage.setItem("token", action.payload.token)
      debugger
      return {...state, session: true}
      return {}
    case "LOGIN":
    //
    return {}
    debugger;
    case "LOGOUT":
    localStorage.removeItem("token")

      return {}
    case "SIGNUP":
      const email = action.email
      const username = action.username
      const password = action.password
      const phone = action.phone
      return {...state, newArtist: { email, username, password, phone } }
    case "USER_CREATED":
    // debugger;
    case "PENDING_CREATE":
      return {...state, creatingUser: true}
    case "ERROR":
    console.log("ERROR AUTH", action);
      return {...state, error: true}
    return state;
      default:
      return state;
    }



  }
