import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Button, Checkbox, Form, Input } from 'semantic-ui-react'
import { Menu, Segment, Fragment } from 'semantic-ui-react'

import {LOGOUT, logout} from '../actions/sessionActions'

class Dashboard extends Component {
  state = { activeItem: 'home' }

  handleLogout = () => {
    this.props.dispatch(this.props.dispatch(logout()))
  }

  handleItemClick = (e, { name }) => this.setState({ activeItem: name })



  render (){
     const { activeItem } = this.state

    return (
      <Fragment>
        <Menu pointing secondary>
          <Menu.Item name='dashboard' active={activeItem === 'dashboard'} onClick={this.handleItemClick} />
          <Menu.Item name='subscribe' active={activeItem === 'subscribe'} onClick={this.handleItemClick} />
          <Menu.Item name='blasts' active={activeItem === 'blasts'} onClick={this.handleItemClick} />
          <Menu.Item name='settings' active={activeItem === 'settings'} onClick={this.handleItemClick}/>
            <Menu.Menu position='right'>
              <Menu.Item
                name='logout'
                active={activeItem === 'logout'}
                onClick={this.handleItemClick}
              />
            </Menu.Menu>
          </Menu>
      </Fragment>
    )
  }
}

export default connect()(Dashboard)
