import initialState from './initialState';
import {browserHistory} from 'react-router';
import { LOGIN_SUCCESS } from '../actions/sessionActions'

export default (state = initialState, action) => {

  switch(action.type) {

    case LOGIN_SUCCESS:
      console.log("JUST LOGGED IN", action.payload.username);
      debugger
      return {...state, sessionsReducer: {...state.sessionsReducer, creatingUser: false}, currentUser: action.payload, authenticated: true}
    case "LOGIN":
    return {}
    case "LOGOUT":
      localStorage.removeItem("token")
      return initialState
    case "PENDING_CREATE":
      return {...state, creatingUser: true}
    case "ERROR":
    console.log("ERROR AUTH", action);
      return {...state, error: true}
    return state;
      default:
      return state;
    }
  }
