import React, { Component, Fragment} from 'react';
import { connect } from 'react-redux';
import { postToBlastCreate } from '../../actions/blastActions'
// import { Button, Checkbox, Form, Input } from 'semantic-ui-react'
import { Menu, Segment, TextArea, Form, Input, Button, Modal, Header, Icon } from 'semantic-ui-react'

class Blasts extends Component {
  state = {content: "", url: "", shortenedLink: "", shortenedLinkId: "", blastConfirmation: false}


  handleFormChange = (fieldInputName, event) => {
    switch(fieldInputName){
      case "content":
        this.setState( {...this.state, content: event.target.value} )
      break;
      case "url":
        this.setState( {...this.state, url: event.target.value} )
      break;
    }
  }

  handleSubmit = () => {
    console.log("LMAO");
    this.props.submitBlast({
      ...this.state,
      id: this.props.currentUserId
    }, "create")
    .then(data => this.setState( { ...this.state, blastConfirmation: true } ))
  }

  handleShortLinkClick = () => {
    this.props.shortenLink({
      content: this.state.content,
      url: this.state.url
    }, "shorten")
    .then(data => {
      debugger
      this.setState({
        content: data.content,
        url: data.url.replace(/^\/|\/$/g, ''),
        shortenedLink: data.shortUrl,
        shortenedLinkId: data.shortUrlId
      })
    })
  }

  handleSendBlast = () => {
    this.props.submitBlast({
      ...this.state,
      id: this.props.currentUserId
    }, "send")
    .then(data => this.setState( { ...this.state, blastConfirmation: true } ))
  }



  render(){
    console.log("BLAST STATE", this.state);
    return (
      <Fragment>
        <Form onSubmit={this.handleSubmit}>
          <TextArea
            value={this.state.content}
            id="blast-content"
            placeholder='Text blast content'
            onChange={(event) => this.handleFormChange("content", event)}
          />
          <Input
          value={this.state.url}
          placeholder='Text blast url'
          onChange={(event) => this.handleFormChange("url", event)}
          />
          <Button type="button" onClick={this.handleShortLinkClick}>Shorten Link</Button>
          <Input
          action={{ color: 'teal', labelPosition: 'right', icon: 'copy', content: 'Copy' }}
          defaultValue='http://ww.short.url/c0opq'
          value={this.state.shortenedLink}
          placeholder='Shortened url'
          />
          <br/>
          <Button type='submit'>Send Blast</Button>
        </Form>

        <Modal open={this.state.blastConfirmation} basic size='small'>
          <Header textAlign="center" content='Send Blast?' />
          <Segment basic textAlign="center">
            <Modal.Actions >
              <Button  color='red'>
                <Icon name='remove' /> No
              </Button>
              <Button onClick={this.handleSendBlast} color='green'>
                <Icon name='checkmark' /> Yes
              </Button>
            </Modal.Actions>
          </Segment>
        </Modal>
      </Fragment>
    )
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    submitBlast: (blastContent, route)=> dispatch(postToBlastCreate(blastContent, route)),
    shortenLink: (blastContent, route)=> dispatch(postToBlastCreate(blastContent, route)),
    sendblast: (blastContent, route)=> dispatch(postToBlastCreate(blastContent, route))
  }
}

const mapStateToProps = (state) => {
  return {currentUserId: state.sessionsReducer.currentUser.id}
}

export default connect(mapStateToProps, mapDispatchToProps)(Blasts)
