import React, { Component, Fragment } from 'react';
import { connect } from 'react-redux';
import { Button, Checkbox, Form, Input } from 'semantic-ui-react'
import { Menu, Segment, Header } from 'semantic-ui-react'

import {LOGOUT, logout} from '../actions/sessionActions'

class Dashboard extends Component {
  state = { activeItem: 'home' }

  handleLogout = () => {
    this.props.dispatch(this.props.dispatch(logout()))
  }

  handleItemClick = (e, { name }) => this.setState({ activeItem: name })



  render (){

     const { activeItem } = this.state

    return (
      <Fragment>
        <h1>DASHBOARD</h1>
        {<Header as='h1' textAlign='center'>Fanbase: {`${this.props.fanCount}`}</Header>}

      </Fragment>
    )
  }

}

const mapStateToProps = (state) => {
    return {fanCount: state.sessionsReducer.currentUser.fanbase.count}

}

export default connect(mapStateToProps)(Dashboard)
