import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Button, Checkbox, Form, Input, Header } from 'semantic-ui-react'


import { postToSubscriptionCreate } from '../../actions/subscriptionActions'

class Subscribe extends Component {
  state={phone: "", zip: "", payload: {}, FanView: false}


  componentDidUpdate(){
    const slug = this.props.match.params.slug
    if(slug){
      this.fetchArtistShowPage(slug)
    } else {
      // this.fetchArtistShowPage(this.props.currentUser.username)
    }
  }

  fetchArtistShowPage = (slug) => {
    fetch(`http://localhost:3000/api/v1/subscribe/${slug}`)
    .then(r => r.json())
    .then(data => {
      this.setState({...this.state, payload: data.payload, FanView: true})
    })
  }

  handleFormChange = (fieldInputName, event) => {
    switch(fieldInputName){
      case "phone":
      this.setState( {...this.state, phone: event.target.value} )
      break;
      case "zip":
      this.setState( {...this.state, zip: event.target.value} )
      case "url":
      this.setState( {...this.state, url: `http://localhost:3001/subscribe/${event.target.value}`} )
      break;
    }
  }

  dispatchSubscription = () => {
    this.props.postToSubscriptionCreate(this.state)
  }

  renderEditView = () => {
    if(this.props.currentUser){
      return (
        <Form.Field>
          <Input value={this.state.url} placeholder='Enter custom url'name="password" label='http://localhost:3001/subscribe/'  onChange={(event) => this.handleFormChange("url", event)}/>
        </Form.Field>

      )
    }
  }

  render (){
        console.log("PROPSSSSS", this.props.currentUser);
    return (
      <div>
      <Header as='h1' textAlign='center'>{`${this.state.payload.title}`}</Header>
      <br/>
      <br/>
      <br/>
      <br/>
      <br/>
      <br/>
      <br/>
        <Form onSubmit={this.dispatchSubscription}>
        <Form.Group inline>
          <Form.Field required>
            <label>Phone Number</label>
            <Input placeholder='(xxx)' maxLength="3" type="number" step="0.01"/>
          </Form.Field>
          <Form.Field required>
            <Input placeholder='xxx' maxLength="3" type="number"/>
          </Form.Field>
          <Form.Field required>
            <Input placeholder='xxxx' maxLength="4" type="number"/>
          </Form.Field>
        </Form.Group>
          <Form.Field required>
            <Input placeholder='Zipcode' value={this.state.zip} name="password"  onChange={(event) => this.handleFormChange("zip", event)}/>
          </Form.Field>
          <Button type='submit'>Subscribe</Button>
          {this.renderEditView()}
        </Form>
      </div>
    )
  }
}
const mapDispatchToProps = (dispatch) => {
  console.log("SUB - DP", dispatch);
  return {postToSubscriptionCreate: (newArtist)=> dispatch(postToSubscriptionCreate(newArtist))}
}

const mapStateToProps = (state) => {
  return {currentUser: state.sessionsReducer.currentUser}
}

export default connect(mapStateToProps, mapDispatchToProps)(Subscribe)
