import React, { Component } from 'react';
import { connect } from 'react-redux';
import {Card} from 'semantic-ui-react'
import { getBlasts } from '../../../actions/blastActions'
import BlastCard from './BlastCard'

class BlastContainer extends Component {
  state = { blasts: [] }

  componentDidMount(){
    this.props.getBlasts(this.props.token, "index")
    .then(data => {
      this.setState( { blasts: data.blasts } )
    })
  }

  //If there are blasts to render..
  conditionallyRenderBlastCards = () => {
    return this.state.blasts ? this.renderBlastCards(this.state.blasts) : <h1> No blasts yet. </h1>
  }

  renderBlastCards = (blasts) => {
    return blasts.map(blast => {
      return <BlastCard blast={blast}/>
    })
  }

  render(){
    return (
      <div id="blast-card-container">
            <Card.Group centered itemsPerRow={1}>
              {this.conditionallyRenderBlastCards()}
            </Card.Group>
      </div>
    )
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    getBlasts: (token, route) => dispatch(getBlasts(token, route))
  }
}

const mapStateToProps = (state) => {
  debugger
  return {
    token: state.sessionsReducer.currentUser.token,
    blasts: state.sessionsReducer.currentUser.blasts
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(BlastContainer)
