import React from 'react';
import {Component} from 'react';
import './App.css';
import ArtistSignup from './components/ArtistSignup'
import { postToArtistCreate } from './actions/sessionActions'
import { connect } from 'react-redux';
import Dashboard from './components/Dashboard'
import Login from './components/Login'
import { Button } from 'semantic-ui-react'
import {LOGIN, autoLogin} from './actions/sessionActions'


class App extends Component {

  componentDidMount(){
    console.log("MOUNTED", this.checkForToken());
  }

  renderrIfLoggedIn = () => {
    if(this.props.currentUser){
      console.log("WORKED");
      return <Dashboard />
    }
  }

  checkForToken = () => {
    const token = localStorage.token
    return token ? "this.props.fetchArtistCreate(autoLogin(token))" : null
  }

  render(){
    console.log("APP PROPS", this.props);
    return (
      <div id="sign-up-container">
        <ArtistSignup />
        <br/>
        <br/>
        <br/>
        <br/>
        <Login />
        {this.renderrIfLoggedIn()}
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  if(state.sessionsReducer.currentUser){
    const id = state.sessionsReducer.currentUser.id
    const authenticated = state.sessionsReducer.authenticated
    return {currentUser: { id , authenticated  }}
  } else {
    return state
  }
}

const mapDispatchToProps = (dispatch) => {
  return {fetchArtistCreate: (token)=> dispatch(postToArtistCreate(token, "auto_login"))}
}

export default connect(mapStateToProps, mapDispatchToProps)(App);
