import React, { Component } from 'react';
import { connect } from 'react-redux';
import {Link, withRouter} from 'react-router-dom'
import { Menu, Segment, Button, Checkbox, Form, Input, Grid } from 'semantic-ui-react'
import {  } from 'semantic-ui-react'

import {LOGOUT, logout} from '../actions/sessionActions'

class Dashboard extends Component {
  state = { activeItem: 'dashboard' }

  componentDidMount(){
    switch(this.props.location.pathname){
      case "/blasts/draft":
        this.setState({activeItem: 'draft blast'})
      break;
      case "/dashboard":
        this.setState({activeItem: 'dashboard'})
      break;
      case "/subscribe/edit":
        this.setState({activeItem: 'subscribe'})
      break;
      case "/blasts":
        this.setState({activeItem: 'blasts'})
      break;
      case "/settings":
        this.setState({activeItem: 'settings'})
      break;
      default:
        this.setState({activeItem: 'dashboard'})
      break;
    }
  }

  handleLogout = () => {
    this.props.dispatch(this.props.dispatch(logout()))
  }

  handleItemClick = (e, { name }) => {
    this.setState({ activeItem: name })
  }

  render (){
     const { activeItem } = this.state
    return (
      <Segment inverted>
        <Menu compact inverted pointing secondary size='massive'>
          <Grid columns='equal' centered id="nav-bar-grid">
            <Grid.Column>
              <Link to='/dashboard'>
                <Menu.Item
                fitted
                name='dashboard'
                active={activeItem === 'dashboard'}
                onClick={this.handleItemClick}
                />
              </Link>
            </Grid.Column>

            <Grid.Column>
              <Link to='/blasts'>
                <Menu.Item
                fitted
                name='blasts'
                active={activeItem === 'blasts'}
                onClick={this.handleItemClick}
                />
              </Link>
            </Grid.Column>

            <Grid.Column >
              <Link to='/blasts/draft'>
                <Menu.Item
                fitted
                name='draft blast'
                active={activeItem === 'draft blast'}
                onClick={this.handleItemClick} />
              </Link>
            </Grid.Column>

            <Grid.Column >
              <Menu.Item
              fitted header>Fan Fam</Menu.Item>
            </Grid.Column>

            <Grid.Column>
                <Link to='/subscribe/edit'>
                  <Menu.Item
                  fitted
                  name='subscribe'
                  active={activeItem === 'subscribe'}
                  onClick={this.handleItemClick}
                  />
                </Link>
            </Grid.Column>

            <Grid.Column>
                <Link to='/settings'>
                  <Menu.Item
                  fitted
                  name='settings'
                  active={activeItem === 'settings'}
                  onClick={this.handleItemClick}/>
                </Link>
            </Grid.Column>
            <Grid.Column>
              <Link to='/'>
                <Menu.Item
                fitted
                  name='logout'
                  active={activeItem === 'logout'}
                  onClick={this.handleLogout}
                />
            </Link>
            </Grid.Column>
          </Grid>
        </Menu>
      </Segment>
    )
  }
}

export default withRouter(connect()(Dashboard))
