import React, { Component, Fragment} from 'react';
import { Grid, Header, Divider, Segment, Statistic} from 'semantic-ui-react'
import { connect } from 'react-redux';


class FanbaseStats extends Component {

  renderStats = (stats) => {
    const arrayOfStats = Object.entries(stats)
     return arrayOfStats.map(stat => {
       return (

        <Segment horizontal >
          <Grid.Column>
            <Statistic size='small' label={stat[0]} value={stat[1]} />
          </Grid.Column>
        </Segment>

         )
       })
    }

  render(){
    return (
      <div id="fanbase-stats">
        <Grid centered columns={2}>
          <Segment>
              <Grid.Row centered columns={2}>
              <Header as='h2'>TOP CITIES</Header>
                {this.renderStats(this.props.stats.top_cities)}
                  <Header as='h2'>TOP STATES</Header>
                {this.renderStats(this.props.stats.top_states)}
              </Grid.Row>
          </Segment>
        </Grid>
      </div>


    )
  }
}

const mapDispatchToProps = (dispatch) => {
  return {

  }
}

const mapStateToProps = (state) => {
  return {stats: state.sessionsReducer.currentUser.fanbase}
}

export default connect(mapStateToProps, mapDispatchToProps)(FanbaseStats)
