export { login, signUp };

const SET_LOGIN_PENDING = 'SET_LOGIN_PENDING';
const SET_LOGIN_SUCCESS = 'SET_LOGIN_SUCCESS';
const SET_LOGIN_ERROR = 'SET_LOGIN_ERROR';
const LOGIN = 'LOGIN'
const SIGNUP = 'SIGNUP'

const login = (email, password) => {
  return {type: LOGIN, email, password}
}

const signUp = (email, username, password, phone) => {
  return {type: SIGNUP, email, username, password, phone}
}

const setLoginPending = (isLoginPending) => {
  return {type: SET_LOGIN_PENDING, isLoginPending}
}

const setLoginSuccess = (isLoginSuccess) => {
  return {type: SET_LOGIN_SUCCESS, isLoginSuccess}
}

const setLoginError = (isLoginError) => {
  return {type: SET_LOGIN_ERROR, isLoginError}
}
