import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Button, Checkbox, Form, Input } from 'semantic-ui-react'


import { postToSubscriptionCreate } from '../../actions/subscriptionActions'

class Subscribe extends Component {
  state={phone: "", zip: "", url: "http://localhost:3001/subscribe/"}

  // handleLogout = () => {
  //   this.props.dispatch(this.props.dispatch(logout()))
  // }

  handleFormChange = (fieldInputName, event) => {
    switch(fieldInputName){
      case "phone":
      this.setState( {...this.state, phone: event.target.value} )
      break;
      case "zip":
      this.setState( {...this.state, zip: event.target.value} )
      case "url":
      this.setState( {...this.state, url: this.state.url + event.target.value} )
      break;
    }
  }

  dispatchSubscription = () => {
    this.props.postToSubscriptionCreate(this.state)
  }

  renderEditView = () => {
    return (
      <Form.Field>
        <Input value={this.state.url} name="password" label='http://'  onChange={(event) => this.handleFormChange("url", event)}/>
      </Form.Field>

    )
  }

  render (){
    console.log("FANVIEW", this.state);
    return (
      <div>
        <Form onSubmit={this.dispatchSubscription}>
        <Form.Group inline>
          <Form.Field required>
            <label>Phone Number</label>
            <Input placeholder='(xxx)' maxLength="3" type="number" step="0.01"/>
          </Form.Field>
          <Form.Field required>
            <Input placeholder='xxx' maxLength="3" type="number"/>
          </Form.Field>
          <Form.Field required>
            <Input placeholder='xxxx' maxLength="4" type="number"/>
          </Form.Field>
        </Form.Group>
          <Form.Field required>
            <Input placeholder='Zipcode' value={this.state.zip} name="password"  onChange={(event) => this.handleFormChange("zip", event)}/>
          </Form.Field>
          <Button type='submit'>Subscribe</Button>
          {this.renderEditView()}
        </Form>
      </div>
    )
  }
}
const mapDispatchToProps = (dispatch) => {
  console.log("SUB - DP", dispatch);
  return {postToSubscriptionCreate: (newArtist)=> dispatch(postToSubscriptionCreate(newArtist))}
}

const mapStateToProps = (state) => {
  console.log("SUB - STATE", state);
}

export default connect(mapStateToProps, mapDispatchToProps)(Subscribe)
