import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Button, Checkbox, Form, Input, Header, Modal } from 'semantic-ui-react'


import { updateSubscriptionProps } from '../../actions/subscriptionActions'

class Subscribe extends Component {
  state={ slug: "", title: "", editTitle: false, error: false}


  componentDidMount(){
    this.setState({...this.state, slug: this.props.subscriptionProps.slug, title: this.props.subscriptionProps.title})
    }

  handleFormChange = (fieldInputName, event) => {
    switch(fieldInputName){
      case "slug":
        this.setState( {...this.state, slug: event.target.value} )
      break;
      case "title":
        this.setState( {...this.state, title: event.target.value} )
      break;
    }
  }

  handleFormSubmit = (e) => {
    e.preventDefault()
    this.props.updateSubscriptionProps({
      slug: this.state.slug,
      pageId: this.props.subscriptionProps.pageId,
      title: this.state.title
    })
    .then(resp => resp.json())
    .then(data => {
      data.status !== "Success" ? alert("Error") : null
    })
  }

  handleEditTitle = (action, event) => {
    switch(action) {
      case "edit":
        this.setState({...this.state, editTitle: true})
      break;
      case "done":
        this.setState({...this.state, editTitle: false})
      break;
    }
  }


  render (){
    console.log("ARRTIST-SUB", this.state);
    return (
      <div>
      <Header as='h1' textAlign='center'>{`${this.state.title}`}</Header>
        <Button onClick={() => this.handleEditTitle("edit", null)}  labelPosition='right' content='Edit' />

      <Modal open={this.state.editTitle} basic size='tiny'>
        <Header textAlign="center" content='Edit title' />
          <Modal.Content>
          <Form onSubmit={(event) => this.handleEditTitle("done", event)}>
            <Form.Field required>
              <Input value={this.state.title} onChange={(event) => this.handleFormChange("title", event)}/>
            </Form.Field>
            <Button type='submit'>Save</Button>
          </Form>
          </Modal.Content>
      </Modal>

      <br/>
      <br/>
      <br/>
      <br/>
      <br/>
      <br/>
      <br/>
        <Form onSubmit={this.handleFormSubmit}>
        <Form.Group inline>
          <Form.Field required>
            <label>Phone Number</label>
            <Input disabled placeholder='(xxx)' maxLength="3" type="number" step="0.01"/>
          </Form.Field>
          <Form.Field required>
            <Input disabled placeholder='xxx' maxLength="3" type="number"/>
          </Form.Field>
          <Form.Field required>
            <Input disabled placeholder='xxxx' maxLength="4" type="number"/>
          </Form.Field>
        </Form.Group>
          <Form.Field required>
            <Input disabled placeholder='Zipcode' value={this.state.zip} name="password"  onChange={(event) => this.handleFormChange("zip", event)}/>
          </Form.Field>
          <Button type='submit'>Subscribe</Button>
          <Form.Field>
            <Input value={this.state.slug} placeholder='Enter custom url'name="password" label='http://localhost:3001/subscribe/'  onChange={(event) => this.handleFormChange("slug", event)}/>
          </Form.Field>
          <Button type='submit'>Save</Button>
        </Form>

      </div>
    )

  }
}
const mapDispatchToProps = (dispatch) => {
  return {updateSubscriptionProps: (updatedProps)=> dispatch(updateSubscriptionProps(updatedProps))}
}

const mapStateToProps = (state) => {
    return {subscriptionProps: state.sessionsReducer.currentUser.subscriptionProps}
  }


export default connect(mapStateToProps, mapDispatchToProps)(Subscribe)
