import React, { Component } from 'react';
import {Card, Icon, Divider} from 'semantic-ui-react'

const BlastCard = (props) => {

  return (
    <Card fluid transparent color='yellow'>
      <Card.Content textAlign="center" header={props.blast.name} />

      <Card.Content>
        Date Sent: {props.blast.date_sent}<br/>
        Time Sent: {props.blast.time_sent}<br/>
        Blast content: {props.blast.body}
      </Card.Content>
      <Divider fitted/>
      <Card.Content>
        Fans reached: {props.num_of_fans}<br/>
      </Card.Content>

    </Card>
  )
}

export default BlastCard
