export { login, signUp, logout, LOGOUT, LOGIN_SUCCESS};

const SET_LOGIN_PENDING = 'SET_LOGIN_PENDING';
const LOGIN_SUCCESS = 'LOGIN_SUCCESS';
const SET_LOGIN_ERROR = 'SET_LOGIN_ERROR';
const LOGIN = 'LOGIN'
const LOGOUT = 'LOGOUT'
const SIGNUP = 'SIGNUP'

const login = (email, password) => {
  return {type: LOGIN, email, password}
}

const logout = () => {
  return {type: LOGOUT, payload: {}}
}

const signUp = (email, username, password, phone) => {
  return {type: SIGNUP, email, username, password, phone}
}

const setLoginPending = (isLoginPending) => {
  return {type: SET_LOGIN_PENDING, isLoginPending}
}

const setLoginSuccess = (isLoginSuccess) => {
  return {type: LOGIN_SUCCESS, isLoginSuccess}
}

/****************************************************************
FETCHES
****************************************************************/
export const postToArtistCreate = (credentials, loginOrSignup) => {

  return (dispatch) => {
    console.log("TYPE", loginOrSignup);
    dispatch( { type: "PENDING_CREATE" } );
    return fetch(`http://localhost:3000/api/v1/artist/${loginOrSignup}`, {
      method: 'POST',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(credentials)
    })
    .then(response => response.json())
      .then(data => {
      if (data.error){
        dispatch({ type: "ERROR", payload: data })
      } else {
        localStorage.setItem("token", data.token)
        dispatch({ type: LOGIN_SUCCESS, payload: data })
      }
    }

    )
    .catch(error => {
      return error;
    })
  }
}
