import React, { Component } from 'react';
import {Card, Icon, Divider, Statistic, Container} from 'semantic-ui-react'

const BlastCard = (props) => {
  return (
    <Card fluid transparent color='yellow'>
      <Card.Content textAlign="center" header={props.blast.name} />
        <Card.Content textAlign='center'>
            <Statistic size='small' label='Link clicks' value={props.blast.link_click_count} />
            <Statistic size='small' label='messages delivered' value={props.blast.succesful_messages} />
        </Card.Content>
        <Card.Content textAlign='left'>
          <Icon name='calendar times outline' />
            Date Sent: {props.blast.date_sent}<br/>
          <Icon name='time' />
            Time Sent: {props.blast.time_sent}
        </Card.Content>

    </Card>
  )
}

const successRate = (successful, failed) => {
  const successfulSms = parseInt(successful)
  const failedSms = parseInt(failed)
  const total = successfulSms + failedSms
  return total - failedSms / total * 100
}


export default BlastCard

