import React, { Component, Fragment } from 'react';
import { connect } from 'react-redux';
import { Statistic, Header, Icon, Button} from 'semantic-ui-react'
import FanbaseStats from './ArtistViews/FanbaseStats'
import Clipboard from 'clipboard';

import {logout} from '../actions/sessionActions'

class Dashboard extends Component {
  state = { activeItem: 'home' }

  componentDidMount(){
    new Clipboard('#copy-url-btn');
      this.setState({
        ...this.state,
        slug: this.props.subscriptionProps.slug,
        title: this.props.subscriptionProps.title
      })
    }

  handleLogout = () => {
    this.props.dispatch(this.props.dispatch(logout()))
  }

  handleItemClick = (e, { name }) => this.setState({ activeItem: name })
  //TODO: add placeholder stats in top cities + states if none exist
  render (){
    const url = `http://localhost:3001/subscribe/${this.state.slug}`//FOR COMPYING TO CLIPBOARD
    return (
      <Fragment>
          <div id="user-subscription-copy">
            <Header inverted as='h2' content="My Subscription Page" textAlign='center'/>
              <Button
                size="massive"
                animated='fade'
                icon
                labelPosition='right'
                id="copy-url-btn"
                basic
                inverted
                size="large"
                type='button'
                data-clipboard-text={ url }
                data-clipboard-action="copy"
                onClick={() => this.copySubscriptionLink}
              >
                <Button.Content visible>{`http://localhost:3001/subscribe/${this.props.slug}`}</Button.Content>
                <Button.Content hidden>Click to copy...</Button.Content>
              <Icon name='copy'/>
              </Button>
          </div>
        <FanbaseStats/>
      </Fragment>
    )
  }
}

const mapStateToProps = (state) => {
    return {
      fanCount: state.sessionsReducer.currentUser.fanbase.count,
      topCities: state.sessionsReducer.currentUser.fanbase.top_cities,
      topStates: state.sessionsReducer.currentUser.fanbase.top_states
    }

}

export default connect(mapStateToProps)(Dashboard)

