import React from 'react';
import {Card, Icon, Statistic} from 'semantic-ui-react'

const BlastCard = (props) => {
  return (
    <Card fluid inverted transparent color='yellow'>
      <Card.Content textAlign="center" header={props.blast.name} />
        <Card.Content textAlign='center'>
            <Statistic inverted size='small' label='Link clicks' value={props.blast.link_click_count} />
            <Statistic inverted size='small' label='messages delivered' value={props.blast.succesful_messages} />
        </Card.Content>
        <Card.Content textAlign='left'>
          <Icon inverted name='calendar times outline' />
            Date Sent: {props.blast.date_sent}<br/>
          <Icon inverted name='time' />
            Time Sent: {props.blast.time_sent}
        </Card.Content>

    </Card>
  )
}
export default BlastCard

