import React, { Component, Fragment } from 'react';
import { connect } from 'react-redux';
import { Statistic, Header } from 'semantic-ui-react'

// import {logout} from '../actions/sessionActions'

class GeneralStats extends Component {
  state = { activeItem: 'home' }

  handleLogout = () => {
    this.props.dispatch(this.props.dispatch(logout()))
  }

  handleItemClick = (e, { name }) => this.setState({ activeItem: name })



  render (){
    return (
      <Fragment>
        <Header textAlign='center'>
          <Statistic size='huge'>
            <Statistic.Label>Fans</Statistic.Label>
            <Statistic.Value>{this.props.fanCount}</Statistic.Value>
          </Statistic>
        </Header>
      </Fragment>
    )
  }

}

const mapStateToProps = (state) => {
    return {
      fanCount: state.sessionsReducer.currentUser.fanbase.count,
      topCities: state.sessionsReducer.currentUser.fanbase.top_cities,
      topStates: state.sessionsReducer.currentUser.fanbase.top_states
    }

}

export default connect(mapStateToProps)(GeneralStats)
