import React, { Component } from 'react';
import { connect } from 'react-redux';
import {Link} from 'react-router-dom'
import { Button, Checkbox, Form, Input } from 'semantic-ui-react'
import { Menu, Segment } from 'semantic-ui-react'

import {LOGOUT, logout} from '../actions/sessionActions'

class Dashboard extends Component {
  state = { activeItem: 'dashboard' }

  handleLogout = () => {
    this.props.dispatch(this.props.dispatch(logout()))
  }

  //TODO: Move activeItem to global memory?
  handleItemClick = (e, { name }) => this.setState({ activeItem: name })

  render (){
    return (
      <Segment inverted>
        <Menu inverted pointing secondary size='massive'>
          <Link to='/dashboard'>
            <Menu.Item name='dashboard' active={this.state.activeItem === 'dashboard'} onClick={this.handleItemClick} />
          </Link>
          <Link to='/subscribe/edit'>
            <Menu.Item name='subscribe' active={this.state.activeItem === 'subscribe'} onClick={this.handleItemClick} />
          </Link>
          <Link to='/blasts'>
            <Menu.Item name='blasts' active={this.state.activeItem === 'blasts'} onClick={this.handleItemClick} />
          </Link>
          <Link to='/blasts/draft'>
            <Menu.Item name=' draft blasts' active={this.state.activeItem === 'draft'} onClick={this.handleItemClick} />
          </Link>
          <Link to='/settings'>
            <Menu.Item name='settings' active={this.state.activeItem === 'settings'} onClick={this.handleItemClick}/>
          </Link>
