import React, { Component } from 'react';
import {Card, Icon} from 'semantic-ui-react'

const BlastCard = (props) => {
  debugger
  return (
    <Card fluid>
      <Card.Content textAlign="center" header={props.blast.name} />

      <Card.Content>
        Blast content: {props.blast.body}
        Fans reached: {props.num_of_fans}
      </Card.Content>
      <Divider />
      <Card.Content>
        Blast conttent: {props.blast.body}
        Fans reached:
        Date Sent: {props.blast.date_sent}<br/>
        Time Sent: {props.blast.time_sent}
      </Card.Content>

    </Card>
  )
}

export default BlastCard
