import React, { Component } from 'react';
import { connect } from 'react-redux';
import {Link, withRouter} from 'react-router-dom'
import { Button, Checkbox, Form, Input } from 'semantic-ui-react'
import { Menu, Segment } from 'semantic-ui-react'

import {LOGOUT, logout} from '../actions/sessionActions'

class Dashboard extends Component {
  state = { activeItem: 'dashboard' }

  componentDidMount(){
    switch(this.props.location.pathname){
      case "/blasts/draft":
        this.setState({activeItem: 'draft blast'})
      break;
      case "/dashboard":
        this.setState({activeItem: 'dashboard'})
      break;
      case "/subscribe/edit":
        this.setState({activeItem: 'subscribe'})
      break;
      case "/blasts":
        this.setState({activeItem: 'blasts'})
      break;
      case "/settings":
        this.setState({activeItem: 'settings'})
      break;
      default:
        this.setState({activeItem: 'dashboard'})
      break;
    }
  }

  handleLogout = () => {
    this.props.dispatch(this.props.dispatch(logout()))
  }

  handleItemClick = (e, { name }) => {
    this.setState({ activeItem: name })
  }

  render (){
     const { activeItem } = this.state
    return (
      <Segment inverted>
        <Menu borderless inverted pointing secondary size='massive'>
          <Link to='/dashboard'>
            <Menu.Item name='dashboard' active={activeItem === 'dashboard'} onClick={this.handleItemClick} />
          </Link>
          <Link to='/subscribe/edit'>
            <Menu.Item name='subscribe' active={activeItem === 'subscribe'} onClick={this.handleItemClick} />
          </Link>
          <Link to='/blasts'>
            <Menu.Item name='blasts' active={activeItem === 'blasts'} onClick={this.handleItemClick} />
          </Link>
          <Link to='/blasts/draft'>
            <Menu.Item name='draft blast' active={activeItem === 'draft blast'} onClick={this.handleItemClick} />
          </Link>
          <Link to='/settings'>
            <Menu.Menu position='right' name='settings' active={activeItem === 'settings'} onClick={this.handleItemClick}/>
          </Link>
            <Menu.Menu position='right'>
            <Link to='/'>
              <Menu.Item
                name='logout'
                active={activeItem === 'logout'}
                onClick={this.handleLogout}
              />
            </Link>
            </Menu.Menu>
          </Menu>
      </Segment>
    )
  }
}

export default withRouter(connect()(Dashboard))
