import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Button, Checkbox, Form, Input } from 'semantic-ui-react'
import {LOGIN, login} from '../actions/sessionActions'
import { postToArtistCreate } from '../actions/sessionActions'

class Login extends Component {

  state = {email: "", password: ""}

  handleFormChange = (fieldInputName, event) => {
    switch(fieldInputName){
      case "email":
      this.setState( {...this.state, email: event.target.value} )
      break;
      case "password":
      this.setState( {...this.state, password: event.target.value} )
      break;
    }
  }


  dispatchLogin = () => {
    this.props.fetchArtistCreate(login(this.state.email, this.state.password))
  }


  render (){

    return (
      <div>
        <Form onSubmit={this.dispatchLogin}>
          <Form.Field>
            <Input size='huge' placeholder='Email' value={this.state.email} name="email" onChange={(event) => this.handleFormChange("email", event)}/>
          </Form.Field>
          <Form.Field>
            <Input size='huge' placeholder='Password' type="password" value={this.state.password} name="password"  onChange={(event) => this.handleFormChange("password", event)}/>
          </Form.Field>
          <Button type='submit'>Login</Button>
        </Form>
      </div>
    )
  }
}

const mapDispatchToProps = (dispatch) => {
  return {fetchArtistCreate: (newArtist)=> dispatch(postToArtistCreate(newArtist, "login"))}
}

export default connect(null, mapDispatchToProps)(Login)
