import React, { Component, Fragment} from 'react';
import { Grid } from 'semantic-ui-react'

import { connect } from 'react-redux';


class FanbaseStats extends Component {

  renderStats = (stats, type) => {
    const arrayOfStats = Object.entries(stats)
     return arrayOfStats.map(stat => {
       return (
         <Fragment>
          <Grid.Column centered floated='left'>
            <h4>{stat[0]}</h4>
          </Grid.Column>
          <Grid.Column centered floated='right'>
            <h4>{stat[1]}</h4>
          </Grid.Column>
          </Fragment>
         )
       })
    }

  render(){
    return (
      <Fragment>
        <Grid centered columns={2} >
          <Grid.Row>
          {this.renderStats(this.props.stats.top_cities, "Cities")}
          </Grid.Row>
        </Grid>
      </Fragment>


    )
  }
}

const mapDispatchToProps = (dispatch) => {
  return {

  }
}

const mapStateToProps = (state) => {
  return {stats: state.sessionsReducer.currentUser.fanbase}
}

export default connect(mapStateToProps, mapDispatchToProps)(FanbaseStats)
