import React from 'react';
import {Card, Icon, Statistic, Grid, Header} from 'semantic-ui-react'

const BlastCard = (props) => {
  return (

          <Grid.Column centered width={4}>
              <Statistic inverted size='small' label='Link clicks' value={props.blast.link_click_count} />
              <Statistic inverted size='small' label='messages delivered' value={props.blast.succesful_messages} />
            <Header
              inverted
              size='tiny'>
              <Icon inverted name='calendar times outline' />
              <Header.Content>Date Sent: {props.blast.date_sent}</Header.Content>
            </Header>

            <Header
            inverted
            size='tiny'>
            <Icon inverted name='time' />
            <Header.Content>Time Sent: {props.blast.time_sent}</Header.Content>
            </Header>


          </Grid.Column>
  )
}
export default BlastCard

