import React, { Component } from 'react';
import { Button, Checkbox, Form } from 'semantic-ui-react'

export class ArtistSignup extends Component {
  state = {email: "", username: "", password: "", phone: ""}

  handleFormChange = (event) => {
    switch(event.target.name){
      case "email":
        this.setState( {...this.state, email: event.target.value} )
      case "username":
        this.setState( {...this.state, username: event.target.value} )
      case "password":
        this.setState( {...this.state, password: event.target.value} )
    }

  }

  signupForm = () => {
    console.log("-----", this.state.email);
    return (
      <Form>
    <Form.Field>
      <input placeholder='Email' value={this.state.email} name="email" onChange={this.handleFormChange}/>
    </Form.Field>
    <Form.Field>
      <input placeholder='Username' value={this.state.username} name="username" onChange={this.handleFormChange}/>
    </Form.Field>
    <Form.Field>
      <input placeholder='Password' value={this.state.password} name="password"  onChange={this.handleFormChange}/>
    </Form.Field>
    <Form.Field>
      <input placeholder='Phone' value={this.state.phone} name="phone" onChange={this.handleFormChange}/>
    </Form.Field>
    <Button type='submit'>Submit</Button>
  </Form>
    )
  }

  render(){
    return this.signupForm()
  }

}

export default ArtistSignup
