import React from 'react';
import {Component, Fragment} from 'react';
import { Button } from 'semantic-ui-react'
import {Switch, Route, Redirect, withRouter } from 'react-router-dom'
import 'semantic-ui-css/semantic.min.css'
import { connect } from 'react-redux';

import './App.css';
import { postToArtistCreate } from './actions/sessionActions'
import ArtistSignup from './components/ArtistSignup'
import Dashboard from './components/Dashboard'
import Login from './components/Login'
import Subscribe from './components/FanView/Subscribe'
import SubscriptionPage from './components/ArtistViews/SubscriptionPage'
import Settings from './components/ArtistViews/Settings'
import Blasts from './components/ArtistViews/Blasts'
import Navbar from './components/Navbar'
import Home from './components/Home'
import {LOGIN, autoLogin} from './actions/sessionActions'




class App extends Component {

  state = {unAuthenticated: false}

  componentDidMount(){
    this.checkForToken()
  }

  renderSomething = () => {
    if(this.props.isAuthenticated){
      return <Navbar />
    }
  }

  checkForToken = () => {
    const token = localStorage.token

    return token ? this.props.fetchArtistCreate(autoLogin(token)) : null

  }

  slugParams = ({match}) => {
    return <h1>SUCCESS</h1>
  }


  render(){
    // console.log("APP PROPS", this.props);
    console.log("CURRENT USER IN APP.JS", this.props);
    if (this.props.doneLoading) {
      if(!this.props.isAuthenticated || !this.props.currentUser){
        return (
          <Switch>
          <Route exact path='/' component={Home}/>
          <Route path='/subscribe/:slug' component={Subscribe}/>
          <Redirect to={'/'}/>
          </Switch>
        )
      } else {
        return (
          <Fragment>
          {this.renderSomething()}
          <Switch>
          <Route path='/dashboard' component={Dashboard}/>
          <Route path='/subscribe/edit' component={SubscriptionPage}/>
          <Route path='/settings' component={Settings}/>
          <Route path='/blasts' component={Blasts}/>
          </Switch>
          </Fragment>
        );
      }
    } else {
      // show a spinner
      return <h1>hi</h1>
    }
  }
}

const mapStateToProps = (state) => {
  return {
    isAuthenticated: state.sessionsReducer.authenticated,
    currentUser: state.sessionsReducer.currentUser,
    // isAuthenticated: state.sessionsReducer.currentUser.id,
    doneLoading: !state.sessionsReducer.loading
  }
}

const mapDispatchToProps = (dispatch) => {
  return {fetchArtistCreate: (token)=> dispatch(postToArtistCreate(token, "auto_login"))}
}

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(App))



