import React, { Component, Fragment} from 'react';
import { Grid, Header, Divider, Segment, Statistic} from 'semantic-ui-react'
import { connect } from 'react-redux';


class FanbaseStats extends Component {

  renderStats = (stats, category) => {
    const arrayOfStats = Object.entries(stats)
     return arrayOfStats.map(stat => {
       return (
         <Segment horizontal >
         <Header as='h2'>TOP {`${category}`}</Header>
           <Grid.Column>
            <Statistic size='small' label={stat[0]} value={stat[1]} />
           </Grid.Column>
         </Segment>

       )
     })
   }


  render(){
    return (
      <div id="fanbase-stats">
        <Grid centered stackable columns={2}>
          <Segment>


                {this.renderStats(this.props.stats.top_cities, "CITIES")}
                  <Header as='h2'>TOP STATES</Header>
                {this.renderStats(this.props.stats.top_states), "STATES"}

          </Segment>
        </Grid>
      </div>


    )
  }
}

const mapDispatchToProps = (dispatch) => {
  return {

  }
}

const mapStateToProps = (state) => {
  return {stats: state.sessionsReducer.currentUser.fanbase}
}

export default connect(mapStateToProps, mapDispatchToProps)(FanbaseStats)
