import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Button, Checkbox, Form, Input } from 'semantic-ui-react'
import { signUp } from '../actions/signup-login'

export class ArtistSignup extends Component {

  state = {email: "", username: "", password: "", phone: ""}

  handleFormChange = (fieldInputName, event) => {

    switch(fieldInputName){
      case "email":
        this.setState( {...this.state, email: event.target.value} )
        break;
      case "username":
        this.setState( {...this.state, username: event.target.value} )
        break;
      case "password":
        this.setState( {...this.state, password: event.target.value} )
        break;
      case "phone":
        this.setState( {...this.state, phone: event.target.value} )
        break;
    }

  }

  signupForm = () => {
    // console.log("STATE", this.state);
    return (
      <Form onSubmit={this.handleSubmit}>
    <Form.Field>
      <Input size='huge' placeholder='Email' value={this.state.email} name="email" onChange={(event) => this.handleFormChange("email", event)}/>
    </Form.Field>
    <Form.Field>
      <Input fluid transparent placeholder='Username' value={this.state.username} name="username" onChange={(event) => this.handleFormChange("username", event)}/>
    </Form.Field>
    <Form.Field>
      <Input placeholder='Password' type="password" value={this.state.password} name="password"  onChange={(event) => this.handleFormChange("password", event)}/>
    </Form.Field>
    <Form.Field>
      <Input placeholder='Phone' value={this.state.phone} name="phone" onChange={(event) => this.handleFormChange("phone", event)}/>
    </Form.Field>
    <Button type='submit'>Submit</Button>
  </Form>
    )
  }

  dispatchSignup = () => {
    this.props.dispatch(signUp(this.state.email, this.state.username, this.state.password, this.state.phone))
  }

  handleSubmit = (event) => {
    event.preventDefault()
    this.dispatchSignup()
  }

  render(){
    return this.signupForm()
  }

}



export default connect()(ArtistSignup);
