import React, { Component, Fragment } from 'react';
import { connect } from 'react-redux';
import { postToBlastCreate } from '../actions/blastActions'
// import { Button, Checkbox, Form, Input } from 'semantic-ui-react'
import { Menu, Segment, TextArea, Form, Input, Button } from 'semantic-ui-react'

class Blasts extends Component {
  state = {content: "", url: ""}
  render(){
    return (
      <Form>
        <TextArea id="blast-content" placeholder='Text blast content' />
        <Input placeholder='Text blast url'/>
        <Button type='submit'>Send Blast</Button>
      </Form>
    )
  }
}

const mapDispatchToProps = (dispatch) => {
  return {sendBlast: (blastContent)=> dispatch(postToBlastCreate(blastContent))}
}

export default connect()(Blasts)
