import React, { Component } from 'react';
import { connect } from 'react-redux';
import {Card, Icon} from 'semantic-ui-react'
import { postToBlastCreate } from '../../../actions/blastActions'

class BlastCard extends Component {

  componentDidMount(){
    this.props.getBlasts
  }

  render(){
    return (
      <Card fluid>
        <Card.Content header='About Amy' />
        <Card.Content description="Hey there" />
        <Card.Content extra>
          <Icon name='user' />
          4 Friends
        </Card.Content>
      </Card>
    )
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    // submitBlast: (blastContent, route)=> dispatch(postToBlastCreate(blastContent, route)),
    // shortenLink: (blastContent, route)=> dispatch(postToBlastCreate(blastContent, route)),
    // sendblast: (blastContent, route)=> dispatch(postToBlastCreate(blastContent, route))
  }
}

const mapStateToProps = (state) => {
  return {
    token: state.sessionsReducer.currentUser.token
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(BlastCard)
