import React, { Component, Fragment } from 'react';
import { connect } from 'react-redux';
import { Segment, Button, Divider, Form, Grid, Header, Image, Modal } from 'semantic-ui-react'
import 'semantic-ui-css/semantic.min.css'

import Login from './Login'
import ArtistSignup from './ArtistSignup'
import {LOGOUT, logout} from '../actions/sessionActions'

class Home extends Component {
  state = { open: false }

  show = dimmer => () => this.setState({ dimmer, open: true })
  close = () => this.setState({ open: false })

  render(){
      const { open, dimmer } = this.state
    return(
      <Segment placeholder>
         <Grid container columns={2} relaxed='very' stackable>
            <Grid.Column>
              <Login />
            </Grid.Column>

           <Grid.Column verticalAlign='middle'>
           <Modal trigger={<Button size='huge' onClick={this.show('blurring')}>Signup</Button>} dimmer={dimmer} open={open} onClose={this.close}>
              <Modal.Header>Signup</Modal.Header>
                <Modal.Content>
                  <ArtistSignup />
                </Modal.Content>
              </Modal>
           </Grid.Column>
         </Grid>


       </Segment>
    )
  }
}

export default connect()(Home)

